document.addEventListener("DOMContentLoaded",()=>{const e=document.body,t=document.getElementById("theme-toggle"),d=localStorage.getItem("theme");"dark"===d&&e.classList.add("dark-mode"),t.addEventListener("click",()=>{e.classList.toggle("dark-mode");const t=e.classList.contains("dark-mode");localStorage.setItem("theme",t?"dark":"light")})});